/**
 *  Get the values of those stupid ASP.NET form values
 *  @param String $htmlFormPage - source code of the page that contains the HTML form
 *  @param String $inputName - the name (and the ID) of the input that is of interest
 *  @return String $value - the value of the requested input 
 *   
 */  
function getStupidAspValue($htmlFormPage, $inputName)
{    
    $pattern = '|<input type=\"hidden\" name=\"'.$inputName.'\" id=\"'.$inputName.'\" value=\"(.+)\" \\/>|'; 
    $matches = array();
    preg_match($pattern, $htmlFormPage, $matches);

    return $matches[1];
}


